// src/DurgaModel.js
import React, { useState } from 'react';
import { Canvas, useLoader } from '@react-three/fiber';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from '@react-three/drei';

const Model = ({ url, position }) => {
  const gltf = useLoader(GLTFLoader, url);
  return (
    <primitive
      object={gltf.scene}
      scale={0.5} // Adjust the scale if necessary
      position={position} // Use position prop
    />
  );
};

const DurgaModel = () => {
  const [position, setPosition] = useState([0, 0, 0]); // Default position
  const modelUrl = '/models/DurgaModel.glb'; // Path relative to public directory

  // Function to update position (example)
  const handlePositionChange = (x, y, z) => {
    setPosition([x, y, z]);
  };

  return (
    <div>
      <Canvas>
        <ambientLight intensity={0.5} />
        <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} />
        <Model url={modelUrl} position={position} />
        <OrbitControls />
      </Canvas>
      <div style={{ position: 'absolute', top: 20, left: 20 }}>
        <button onClick={() => handlePositionChange(position[0] + 1, position[1], position[2])}>
          Move Right
        </button>
        <button onClick={() => handlePositionChange(position[0] - 1, position[1], position[2])}>
          Move Left
        </button>
        <button onClick={() => handlePositionChange(position[0], position[1] + 1, position[2])}>
          Move Up
        </button>
        <button onClick={() => handlePositionChange(position[0], position[1] - 1, position[2])}>
          Move Down
        </button>
        <button onClick={() => handlePositionChange(position[0], position[1], position[2] + 1)}>
          Move Forward
        </button>
        <button onClick={() => handlePositionChange(position[0], position[1], position[2] - 1)}>
          Move Backward
        </button>
      </div>
    </div>
  );
};

export default DurgaModel;
